#include <iostream>using namespace std;bool b[1000][1000];int t[1000];int LargestSameSequence(int n){ int max = 0; int cur = 0; for (int i=0; i<n; ++i) {  if (t[i]!=0)  {   if(cur>max)    max = cur;   cur = 0;  }  else   cur++; } if(cur>max)  max = cur; return max;}void main(){ int k,m,n,i,j,h,x,max,cur; char c; cin>>k; for (h=0; h<k; ++h) {  cin>>m>>n;  memset( &b[0][0], 0, sizeof(b) );  for (i=0; i<m; ++i)  {   for (j=0; j<n; ++j)   {    scanf("%c",&c);    while(c!='R' && c!='F')      cin>>c;    b[i][j] = (c=='R');   }  }  //  max = 0;  for(i=0; i<m; ++i)  {   memset(t, 0, sizeof(t));   if((m-i)*n<max)    break;   for(j=i; j<m; ++j)   {    for(x=0; x<n; ++x)     t[x] += b[j][x];    cur = LargestSameSequence(n) * (j-i+1);    if(max<cur)     max = cur;   }  }  cout<<max*3<<endl; }}